pragma solidity >=0.4.21 <0.6.0;
import 'openzeppelin-solidity/contracts/lifecycle/Pausable.sol';
import 'openzeppelin-solidity/contracts/ownership/Ownable.sol';
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";

contract ContactInvite is Ownable, Pausable {

  ERC20 public token;
  uint public requiredStake;
  uint public creationBlock;

  struct InviteRequest {
    uint stake;
    uint block;
  }

  // Stores the state of users available
  // to contact and their required stake
  // mapping (address => uint256) public registeredUsers;
  mapping (address => mapping (bytes32 => InviteRequest)) public inviteRequests;

  struct Staker {
    uint256 stakedAmount;
    address stakerAddress;
  }

  constructor(address _tokenAddress) public {
    token = ERC20(_tokenAddress);
    requiredStake = 10 ether;
    creationBlock = block.number;
  }

  // function register(uint _stake) external {
  //   require(_stake > 0, "Stake should be larger than 0");
  //   registeredUsers[msg.sender] = _stake;
  // }

  // function getRequiredStake(address _address) external view returns (uint256) {
  //   return registeredUsers[_address];
  // }

  // function requiredStake() external view returns (uint) {
  //   return requiredStake;
  // }

  function pendingInviteStake(address recipientAddr, string calldata recipientFeed) external view returns (uint256) {
    bytes32 recipientHash = keccak256(abi.encodePacked(recipientAddr, recipientFeed));
    return inviteRequests[msg.sender][recipientHash].stake;
  }

  // TODO maybe use hashes instead of string

  function sendInvite(address recipientAddr, string calldata recipientFeed, string calldata senderFeed) external {
    // require(registeredUsers[_address] > 0, "User not registered for blockchain invites");
    require(token.allowance(msg.sender, address(this)) > 0, 'Insufficient balance to stake');
    token.transferFrom(msg.sender, address(this), requiredStake);
    bytes32 recipientHash = keccak256(abi.encodePacked(recipientAddr, recipientFeed));
    emit Hashed(recipientHash);
    inviteRequests[msg.sender][recipientHash].stake = requiredStake;
    inviteRequests[msg.sender][recipientHash].block = block.number;
    emit Invited(recipientAddr, recipientFeed, senderFeed, msg.sender);
  }

  function declineAndWithdraw(address inviteSender, string calldata recipientFeed) external {
    bytes32 recipientHash = keccak256(abi.encodePacked(msg.sender, recipientFeed));
    require(inviteRequests[inviteSender][recipientHash].stake > 0, 'No stake to withdraw');
    token.transfer(msg.sender, inviteRequests[inviteSender][recipientHash].stake);
    inviteRequests[inviteSender][recipientHash].stake = 0;
    emit Declined(inviteSender, msg.sender);
  }

  function retrieveStake(address recipientAddr, string calldata recipientFeed, uint8 v, bytes32 r, bytes32 s) external {
    bytes32 recipientHash = keccak256(abi.encodePacked(recipientAddr, recipientFeed));
    require(inviteRequests[msg.sender][recipientHash].stake > 0, 'No stake to withdraw');
    require(verifySig(v,r,s) == recipientAddr, 'Unable to verify recipient signature'); // Verified recipient signed
    token.transfer(msg.sender, inviteRequests[msg.sender][recipientHash].stake);
    emit StakeRetrieved(msg.sender, recipientAddr);
  }

  function verifySig(uint8 v, bytes32 r, bytes32 s) internal returns (address) {
    bytes32 hashedAddress = keccak256(abi.encodePacked(msg.sender));
    bytes32 messageDigest = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hashedAddress));
    emit Hashed(hashedAddress);
    address _address = ecrecover(messageDigest, v, r, s);
    emit Address(_address);
    return _address;
  }


  event Invited(address indexed recipientAddress, string indexed recipientFeed, string senderFeed, address sender);
  event Declined(address indexed sender, address recipient);
  event StakeRetrieved(address indexed sender, address recipient);
  event Hashed(bytes32 hash);
  event Address(address _address);
  event MessageBytes(bytes message);
  event MessageString(string message);
  event IsTrue(bool check);

  event Parsed(bytes1 value);

}
